import React, { useEffect, useState, useRef, useCallback } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import type { RouteProp } from '@react-navigation/native';

declare const console: any;

declare global {
  interface Window {
    performance: {
      memory?: {
        usedJSHeapSize: number;
        totalJSHeapSize: number;
        jsHeapSizeLimit: number;
      };
    };
  }
}

interface MemoryMetrics {
  usedJSHeapSize: number;
  totalJSHeapSize: number;
  jsHeapSizeLimit: number;
  timestamp: number;
}

interface ListenerInfo {
  id: string;
  type: string;
  component: string;
  timestamp: number;
  isActive: boolean;
}

interface MemoryLeakState {
  memoryMetrics: MemoryMetrics[];
  activeListeners: ListenerInfo[];
  cleanupCount: number;
  memoryWarnings: string[];
  lastCleanup: number;
}

export function useMemoryLeakPrevention(componentName: string) {
  const navigation = useNavigation();
  const route = useRoute<RouteProp<Record<string, object | undefined>, string>>();
  const [memoryState, setMemoryState] = useState<MemoryLeakState>({
    memoryMetrics: [],
    activeListeners: [],
    cleanupCount: 0,
    memoryWarnings: [],
    lastCleanup: 0
  });

  const listenerRefs = useRef<Map<string, () => void>>(new Map());
  const memoryIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const cleanupIntervalRef = useRef<NodeJS.Timeout | null>(null);

  // Memory monitoring
  const measureMemory = useCallback(() => {
    try {
      if (typeof window !== 'undefined' && window.performance?.memory) {
        const memory = window.performance.memory;
        const metrics: MemoryMetrics = {
          usedJSHeapSize: memory.usedJSHeapSize,
          totalJSHeapSize: memory.totalJSHeapSize,
          jsHeapSizeLimit: memory.jsHeapSizeLimit,
          timestamp: Date.now()
        };

        setMemoryState(prev => ({
          ...prev,
          memoryMetrics: [...prev.memoryMetrics.slice(-50), metrics] // Keep last 50 measurements
        }));

        // Check for memory warnings
        const usagePercentage = (memory.usedJSHeapSize / memory.jsHeapSizeLimit) * 100;
        if (usagePercentage > 80) {
          const warning = `High memory usage: ${usagePercentage.toFixed(1)}% at ${new Date().toLocaleTimeString()}`;
          setMemoryState(prev => ({
            ...prev,
            memoryWarnings: [...prev.memoryWarnings.slice(-10), warning] // Keep last 10 warnings
          }));
          console.warn('[MemoryLeakPrevention]', warning);
        }
      }
    } catch (error) {
      console.error('[MemoryLeakPrevention] Memory measurement failed:', error);
    }
  }, []);

  // Listener management
  const addListener = useCallback((type: string, handler: () => void) => {
    try {
      const listenerId = `${componentName}-${type}-${Date.now()}`;
      const unsubscribe = navigation.addListener(type as any, handler);

      listenerRefs.current.set(listenerId, unsubscribe);

      const listenerInfo: ListenerInfo = {
        id: listenerId,
        type,
        component: componentName,
        timestamp: Date.now(),
        isActive: true
      };

      setMemoryState(prev => ({
        ...prev,
        activeListeners: [...prev.activeListeners, listenerInfo]
      }));

      console.log('[MemoryLeakPrevention] Listener added:', { id: listenerId, type, component: componentName });

      return listenerId;
    } catch (error) {
      console.error('[MemoryLeakPrevention] Failed to add listener:', error);
      return null;
    }
  }, [navigation, componentName]);

  const removeListener = useCallback((listenerId: string) => {
    try {
      const unsubscribe = listenerRefs.current.get(listenerId);
      if (unsubscribe) {
        unsubscribe();
        listenerRefs.current.delete(listenerId);

        setMemoryState(prev => ({
          ...prev,
          activeListeners: prev.activeListeners.map(listener => 
            listener.id === listenerId ? { ...listener, isActive: false } : listener
          ),
          cleanupCount: prev.cleanupCount + 1,
          lastCleanup: Date.now()
        }));

        console.log('[MemoryLeakPrevention] Listener removed:', listenerId);
      }
    } catch (error) {
      console.error('[MemoryLeakPrevention] Failed to remove listener:', error);
    }
  }, []);

  const cleanupAllListeners = useCallback(() => {
    try {
      let cleanedCount = 0;
      listenerRefs.current.forEach((unsubscribe, listenerId) => {
        unsubscribe();
        cleanedCount++;
      });
      listenerRefs.current.clear();

      setMemoryState(prev => ({
        ...prev,
        activeListeners: prev.activeListeners.map(listener => ({ ...listener, isActive: false })),
        cleanupCount: prev.cleanupCount + cleanedCount,
        lastCleanup: Date.now()
      }));

      console.log('[MemoryLeakPrevention] All listeners cleaned up:', cleanedCount);
    } catch (error) {
      console.error('[MemoryLeakPrevention] Cleanup failed:', error);
    }
  }, []);

  // Auto-cleanup for orphaned listeners
  const checkOrphanedListeners = useCallback(() => {
    const now = Date.now();
    const orphanedListeners = memoryState.activeListeners.filter(
      listener => listener.isActive && (now - listener.timestamp) > 300000 // 5 minutes
    );

    if (orphanedListeners.length > 0) {
      console.warn('[MemoryLeakPrevention] Orphaned listeners detected:', orphanedListeners.length);
      orphanedListeners.forEach(listener => {
        removeListener(listener.id);
      });
    }
  }, [memoryState.activeListeners, removeListener]);

  // Setup memory monitoring
  useEffect(() => {
    // Start memory monitoring
    measureMemory();
    memoryIntervalRef.current = setInterval(measureMemory, 5000); // Every 5 seconds

    // Start orphaned listener cleanup
    cleanupIntervalRef.current = setInterval(checkOrphanedListeners, 30000); // Every 30 seconds

    return () => {
      if (memoryIntervalRef.current) {
        clearInterval(memoryIntervalRef.current);
      }
      if (cleanupIntervalRef.current) {
        clearInterval(cleanupIntervalRef.current);
      }
    };
  }, [measureMemory, checkOrphanedListeners]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      cleanupAllListeners();
    };
  }, [cleanupAllListeners]);

  return {
    memoryState,
    addListener,
    removeListener,
    cleanupAllListeners,
    measureMemory
  };
}

export default function MemoryLeakPrevention() {
  const { memoryState } = useMemoryLeakPrevention('MemoryLeakPrevention');
  const latestMetrics = memoryState.memoryMetrics[memoryState.memoryMetrics.length - 1];
  const activeListeners = memoryState.activeListeners.filter(l => l.isActive);

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Memory Leak Prevention</Text>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Memory Metrics</Text>
        {latestMetrics && (
          <View style={styles.metricsContainer}>
            <Text style={styles.metricText}>
              Used: {(latestMetrics.usedJSHeapSize / 1024 / 1024).toFixed(2)} MB
            </Text>
            <Text style={styles.metricText}>
