import React, { useCallback, useEffect, useState } from 'react';
import { useNavigation } from '@react-navigation/native';

declare const console: any;

interface NavigationState {
  isNavigating: boolean;
  currentRoute: string | null;
  navigationQueue: string[];
  lastNavigation: number;
  navigationErrors: string[];
}

export function useSafeNavigation() {
  const navigation = useNavigation();
  const [navState, setNavState] = useState<NavigationState>({
    isNavigating: false,
    currentRoute: null,
    navigationQueue: [],
    lastNavigation: 0,
    navigationErrors: []
  });

  const safeNavigate = useCallback(async (routeName: string, params?: any) => {
    const now = Date.now();

    // Prevent rapid successive navigations
    if (now - navState.lastNavigation < 100) {
      console.warn('[SafeNavigation] Navigation too rapid, queuing:', routeName);
      setNavState(prev => ({
        ...prev,
        navigationQueue: [...prev.navigationQueue, routeName]
      }));
      return;
    }

    // Prevent concurrent navigation
    if (navState.isNavigating) {
      console.warn('[SafeNavigation] Navigation already in progress, queuing:', routeName);
      setNavState(prev => ({
        ...prev,
        navigationQueue: [...prev.navigationQueue, routeName]
      }));
      return;
    }

    setNavState(prev => ({
      ...prev,
      isNavigating: true,
      currentRoute: routeName
    }));

    try {
      console.log('[SafeNavigation] Navigating to:', routeName);
      await navigation.navigate(routeName as never, params);

      setNavState(prev => ({
        ...prev,
        lastNavigation: now,
        currentRoute: routeName
      }));

      console.log('[SafeNavigation] Navigation successful:', routeName);
    } catch (error) {
      const errorMsg = `Navigation to ${routeName} failed: ${error}`;
      console.error('[SafeNavigation]', errorMsg);

      setNavState(prev => ({
        ...prev,
        navigationErrors: [...prev.navigationErrors.slice(-10), errorMsg]
      }));
    } finally {
      setNavState(prev => ({
        ...prev,
        isNavigating: false
      }));
    }
  }, [navigation, navState.isNavigating, navState.lastNavigation]);

  const safeGoBack = useCallback(async () => {
    if (navState.isNavigating) {
      console.warn('[SafeNavigation] Cannot go back while navigating');
      return;
    }

    setNavState(prev => ({
      ...prev,
      isNavigating: true
    }));

    try {
      console.log('[SafeNavigation] Going back');
      await navigation.goBack();
      console.log('[SafeNavigation] Go back successful');
    } catch (error) {
      const errorMsg = `Go back failed: ${error}`;
      console.error('[SafeNavigation]', errorMsg);

      setNavState(prev => ({
        ...prev,
        navigationErrors: [...prev.navigationErrors.slice(-10), errorMsg]
      }));
    } finally {
      setNavState(prev => ({
        ...prev,
        isNavigating: false
      }));
    }
  }, [navigation, navState.isNavigating]);

  const processQueue = useCallback(async () => {
    if (navState.navigationQueue.length > 0 && !navState.isNavigating) {
      const nextRoute = navState.navigationQueue[0];
      setNavState(prev => ({
        ...prev,
        navigationQueue: prev.navigationQueue.slice(1)
      }));

      await safeNavigate(nextRoute);
    }
  }, [navState.navigationQueue, navState.isNavigating, safeNavigate]);

  // Process navigation queue
  useEffect(() => {
    processQueue();
  }, [processQueue]);

  return {
    safeNavigate,
    safeGoBack,
    navState,
    processQueue
  };
}

