import { useRoute, useNavigation } from '@react-navigation/native';
import type { RouteProp } from '@react-navigation/native';
import { useEffect, useState, useCallback } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import ContextValidator from './ContextValidator';
import TransitionMonitor from './TransitionMonitor';
import DeepNestingTest from './DeepNestingTest';
import { useMemoryLeakPrevention } from './MemoryLeakPrevention';
import { useSafeNavigation } from './SafeNavigationWrapper';

declare const console: any;

interface EnhancedSlotBridgeState {
  isContextValid: boolean;
  contextErrors: string[];
  lastValidation: number;
  navigationAttempts: number;
  memoryLeakProtected: boolean;
  safeNavigationEnabled: boolean;
}

export default function EnhancedSlotBridge() {
  const route = useRoute<RouteProp<Record<string, object | undefined>, string>>();
  const navigation = useNavigation();
  const [bridgeState, setBridgeState] = useState<EnhancedSlotBridgeState>({
    isContextValid: false,
    contextErrors: [],
    lastValidation: 0,
    navigationAttempts: 0,
    memoryLeakProtected: false,
    safeNavigationEnabled: false
  });

  // Memory leak prevention
  const { memoryState, addListener, removeListener, cleanupAllListeners } = useMemoryLeakPrevention('EnhancedSlotBridge');

  // Safe navigation
  const { safeNavigate, safeGoBack, navState } = useSafeNavigation();

  const validateContext = useCallback(() => {
    const errors: string[] = [];
    let isValid = true;

    try {
      // Validate route access
      if (!route) {
        errors.push('Route object is undefined');
        isValid = false;
      } else if (!route.name) {
        errors.push('Route name is undefined');
        isValid = false;
      }

      // Validate navigation object
      if (!navigation) {
        errors.push('Navigation object is undefined');
        isValid = false;
      } else if (typeof navigation.navigate !== 'function') {
        errors.push('Navigation.navigate is not a function');
        isValid = false;
      }

      // Validate navigation state
      if (navigation?.getState && typeof navigation.getState === 'function') {
        const state = navigation.getState();
        if (!state || !Array.isArray(state.routes)) {
          errors.push('Navigation state is invalid');
          isValid = false;
        }
      } else {
        errors.push('Navigation state access failed');
        isValid = false;
      }

      setBridgeState(prev => ({
        ...prev,
        isContextValid: isValid,
        contextErrors: errors,
        lastValidation: Date.now(),
        memoryLeakProtected: true,
        safeNavigationEnabled: true
      }));

      console.log('[EnhancedSlotBridge] Context validation completed:', {
        isValid,
        errors: errors.length,
        routeName: route?.name,
        hasNavigation: !!navigation,
        memoryProtected: true,
        safeNavigation: true
      });

    } catch (error) {
      const errorMsg = `Context validation error: ${error}`;
      setBridgeState(prev => ({
        ...prev,
        isContextValid: false,
        contextErrors: [...prev.contextErrors, errorMsg],
        lastValidation: Date.now()
      }));
      console.error('[EnhancedSlotBridge] Context validation failed:', errorMsg);
    }
  }, [route, navigation]);

  const attemptNavigationRecovery = useCallback(() => {
    if (!bridgeState.isContextValid) {
      console.log('[EnhancedSlotBridge] Attempting navigation recovery...');
      setBridgeState(prev => ({
        ...prev,
        navigationAttempts: prev.navigationAttempts + 1
      }));

      // Attempt to revalidate context
      setTimeout(() => {
        validateContext();
      }, 100);
    }
  }, [bridgeState.isContextValid, validateContext]);

  // Setup navigation listeners with proper cleanup
  useEffect(() => {
    const focusListenerId = addListener('focus', () => {
      console.log('[EnhancedSlotBridge] Screen focused');
      validateContext();
    });

    const blurListenerId = addListener('blur', () => {
      console.log('[EnhancedSlotBridge] Screen blurred');
    });

    const stateListenerId = addListener('state', () => {
      console.log('[EnhancedSlotBridge] Navigation state changed');
      validateContext();
    });

    // Cleanup function
    return () => {
      if (focusListenerId) removeListener(focusListenerId);
      if (blurListenerId) removeListener(blurListenerId);
      if (stateListenerId) removeListener(stateListenerId);
    };
  }, [addListener, removeListener, validateContext]);

  useEffect(() => {
    validateContext();
  }, [validateContext]);

  useEffect(() => {
    if (route?.name) {
      console.log(`[EnhancedSlotBridge] Active route: ${route.name}`);
      console.log(`[EnhancedSlotBridge] Route params:`, route.params);
      console.log(`[EnhancedSlotBridge] Context validation started`);
    }
  }, [route?.name, route?.params]);

  // If context is invalid, show enhanced fallback UI
  if (!bridgeState.isContextValid) {
    return (
      <View style={styles.fallbackContainer}>
        <Text style={styles.fallbackTitle}>Navigation Context Unavailable</Text>
        <Text style={styles.fallbackSubtitle}>Enhanced SlotBridge Fallback Mode</Text>

        <View style={styles.errorContainer}>
          <Text style={styles.errorTitle}>Context Errors:</Text>
          {bridgeState.contextErrors.map((error, index) => (
            <Text key={index} style={styles.errorText}>‚Ä¢ {error}</Text>
          ))}
        </View>

        <View style={styles.recoveryContainer}>
          <Text style={styles.recoveryText}>
            Recovery Attempts: {bridgeState.navigationAttempts}
          </Text>
          <TouchableOpacity style={styles.recoveryButton} onPress={attemptNavigationRecovery}>
            <Text style={styles.recoveryButtonText}>Retry Context</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.protectionContainer}>
          <Text style={styles.protectionText}>Memory Leak Protection: ‚úÖ Active</Text>
          <Text style={styles.protectionText}>Safe Navigation: ‚úÖ Active</Text>
        </View>

        <Text style={styles.fallbackNote}>
          Last validation: {new Date(bridgeState.lastValidation).toLocaleTimeString()}
        </Text>
      </View>
    );
  }

  // Context is valid, show full enhanced SlotBridge functionality
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Enhanced SlotBridge Active</Text>
        <Text style={styles.headerSubtitle}>Context: {route?.name}</Text>
        <Text style={styles.headerProtection}>Memory Protected: ‚úÖ | Safe Navigation: ‚úÖ</Text>
      </View>

      <ContextValidator />
      <TransitionMonitor />
      <DeepNestingTest />
    </View>
  );
}

