import React, { useEffect, useState, useCallback } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';

declare const console: any;

declare global {
  interface Window {
    performance: {
      memory?: {
        usedJSHeapSize: number;
        totalJSHeapSize: number;
        jsHeapSizeLimit: number;
      };
      now(): number;
      getEntriesByType(type: string): PerformanceEntry[];
    };
  }
}

interface SystemMetrics {
  memory: {
    used: number;
    total: number;
    limit: number;
    percentage: number;
  };
  performance: {
    fps: number;
    frameTime: number;
    renderTime: number;
  };
  bundle: {
    size: number;
    loadTime: number;
    parseTime: number;
  };
  navigation: {
    transitions: number;
    averageTransitionTime: number;
    failedTransitions: number;
  };
  components: {
    total: number;
    rendered: number;
    reRendered: number;
  };
  timestamp: number;
}

interface PerformanceAlert {
  id: string;
  type: 'warning' | 'error' | 'critical';
  message: string;
  timestamp: number;
  resolved: boolean;
}

interface DashboardState {
  currentMetrics: SystemMetrics | null;
  historicalMetrics: SystemMetrics[];
  alerts: PerformanceAlert[];
  isMonitoring: boolean;
  lastUpdate: number;
}

export function usePerformanceDashboard() {
  const [dashboardState, setDashboardState] = useState<DashboardState>({
    currentMetrics: null,
    historicalMetrics: [],
    alerts: [],
    isMonitoring: false,
    lastUpdate: 0
  });

  // Measure system memory
  const measureMemory = useCallback(() => {
    try {
      if (typeof window !== 'undefined' && window.performance?.memory) {
        const memory = window.performance.memory;
        return {
          used: memory.usedJSHeapSize,
          total: memory.totalJSHeapSize,
          limit: memory.jsHeapSizeLimit,
          percentage: (memory.usedJSHeapSize / memory.jsHeapSizeLimit) * 100
        };
      }
      return null;
    } catch (error) {
      console.error('[PerformanceDashboard] Memory measurement failed:', error);
      return null;
    }
  }, []);

  // Measure performance metrics
  const measurePerformance = useCallback(() => {
    try {
      // Simulate performance measurements
      const fps = Math.random() * 30 + 30; // 30-60 FPS
      const frameTime = 1000 / fps;
      const renderTime = Math.random() * 20 + 5; // 5-25ms

      return {
        fps: Math.round(fps),
        frameTime: Math.round(frameTime),
        renderTime: Math.round(renderTime)
      };
    } catch (error) {
      console.error('[PerformanceDashboard] Performance measurement failed:', error);
      return { fps: 0, frameTime: 0, renderTime: 0 };
    }
  }, []);

  // Measure bundle metrics
  const measureBundle = useCallback(() => {
    try {
      // Simulate bundle measurements
      const size = Math.random() * 2000000 + 1000000; // 1-3MB
      const loadTime = Math.random() * 2000 + 500; // 500-2500ms
      const parseTime = Math.random() * 1000 + 200; // 200-1200ms

      return {
        size: Math.round(size),
        loadTime: Math.round(loadTime),
        parseTime: Math.round(parseTime)
      };
    } catch (error) {
      console.error('[PerformanceDashboard] Bundle measurement failed:', error);
      return { size: 0, loadTime: 0, parseTime: 0 };
    }
  }, []);

  // Measure navigation metrics
  const measureNavigation = useCallback(() => {
    try {
      // Simulate navigation measurements
      const transitions = Math.floor(Math.random() * 10) + 1;
      const averageTransitionTime = Math.random() * 300 + 100; // 100-400ms
      const failedTransitions = Math.floor(Math.random() * 3);

      return {
        transitions,
        averageTransitionTime: Math.round(averageTransitionTime),
        failedTransitions
      };
    } catch (error) {
      console.error('[PerformanceDashboard] Navigation measurement failed:', error);
      return { transitions: 0, averageTransitionTime: 0, failedTransitions: 0 };
    }
  }, []);

  // Measure component metrics
  const measureComponents = useCallback(() => {
    try {
      // Simulate component measurements
      const total = Math.floor(Math.random() * 50) + 20; // 20-70 components
      const rendered = Math.floor(Math.random() * total) + 10;
      const reRendered = Math.floor(Math.random() * 10);

      return {
        total,
        rendered,
        reRendered
      };
    } catch (error) {
      console.error('[PerformanceDashboard] Component measurement failed:', error);
      return { total: 0, rendered: 0, reRendered: 0 };
    }
  }, []);

  // Collect all metrics
  const collectMetrics = useCallback(() => {
    const memory = measureMemory();
    const performance = measurePerformance();
    const bundle = measureBundle();
    const navigation = measureNavigation();
    const components = measureComponents();

    if (memory) {
      const metrics: SystemMetrics = {
        memory,
        performance,
        bundle,
        navigation,
        components,
        timestamp: Date.now()
      };

      setDashboardState(prev => ({
        ...prev,
        currentMetrics: metrics,
        historicalMetrics: [...prev.historicalMetrics.slice(-100), metrics], // Keep last 100 measurements
        lastUpdate: Date.now()
      }));

      // Check for performance alerts
      checkPerformanceAlerts(metrics);
    }
  }, [measureMemory, measurePerformance, measureBundle, measureNavigation, measureComponents]);

  // Check for performance alerts
  const checkPerformanceAlerts = useCallback((metrics: SystemMetrics) => {
    const alerts: PerformanceAlert[] = [];

    // Memory alerts
    if (metrics.memory.percentage > 90) {
      alerts.push({
        id: `memory-${Date.now()}`,
        type: 'critical',
        message: `Critical memory usage: ${metrics.memory.percentage.toFixed(1)}%`,
        timestamp: Date.now(),
        resolved: false
      });
    } else if (metrics.memory.percentage > 80) {
      alerts.push({
        id: `memory-${Date.now()}`,
        type: 'warning',
        message: `High memory usage: ${metrics.memory.percentage.toFixed(1)}%`,
        timestamp: Date.now(),
        resolved: false
      });
    }

    // Performance alerts
    if (metrics.performance.fps < 30) {
      alerts.push({
        id: `fps-${Date.now()}`,
        type: 'error',
        message: `Low FPS detected: ${metrics.performance.fps} FPS`,
        timestamp: Date.now(),
        resolved: false
      });
    }

    if (metrics.performance.renderTime > 50) {
      alerts.push({
        id: `render-${Date.now()}`,
        type: 'warning',
        message: `Slow render time: ${metrics.performance.renderTime}ms`,
        timestamp: Date.now(),
        resolved: false
      });
    }

    // Bundle alerts
    if (metrics.bundle.size > 5000000) {
      alerts.push({
        id: `bundle-${Date.now()}`,
        type: 'warning',
        message: `Large bundle size: ${(metrics.bundle.size / 1024 / 1024).toFixed(2)}MB`,
        timestamp: Date.now(),
        resolved: false
      });
    }

    // Navigation alerts
    if (metrics.navigation.failedTransitions > 0) {
      alerts.push({
        id: `navigation-${Date.now()}`,
        type: 'error',
        message: `Navigation failures: ${metrics.navigation.failedTransitions}`,
        timestamp: Date.now(),
        resolved: false
      });
    }

    if (alerts.length > 0) {
      setDashboardState(prev => ({
        ...prev,
        alerts: [...prev.alerts, ...alerts]
      }));

      alerts.forEach(alert => {
        console.warn('[PerformanceDashboard] Alert:', alert.message);
      });
    }
  }, []);

  // Start monitoring
  const startMonitoring = useCallback(() => {
    setDashboardState(prev => ({ ...prev, isMonitoring: true }));
    console.log('[PerformanceDashboard] Monitoring started');
  }, []);

  // Stop monitoring
  const stopMonitoring = useCallback(() => {
    setDashboardState(prev => ({ ...prev, isMonitoring: false }));
    console.log('[PerformanceDashboard] Monitoring stopped');
  }, []);

  // Clear alerts
  const clearAlerts = useCallback(() => {
    setDashboardState(prev => ({ ...prev, alerts: [] }));
    console.log('[PerformanceDashboard] Alerts cleared');
  }, []);

  // Collect metrics periodically
  useEffect(() => {
    if (dashboardState.isMonitoring) {
      const interval = setInterval(collectMetrics, 2000); // Every 2 seconds
      return () => clearInterval(interval);
    }
  }, [dashboardState.isMonitoring, collectMetrics]);

  // Initial metrics collection
  useEffect(() => {
    collectMetrics();
  }, [collectMetrics]);

  return {
    dashboardState,
    startMonitoring,
    stopMonitoring,
    clearAlerts,
    collectMetrics
  };
}

