          <Text style={styles.alertsTitle}>Performance Alerts</Text>
          {alerts.slice(-10).map((alert, index) => (
            <View key={alert.id} style={[styles.alertItem, styles[alert.type]]}>
              <Text style={styles.alertMessage}>{alert.message}</Text>
              <Text style={styles.alertTime}>{new Date(alert.timestamp).toLocaleTimeString()}</Text>
            </View>
          ))}
