import React, { useEffect, useState, useCallback } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';

declare const console: any;

interface BundleModule {
  name: string;
  size: number;
  type: 'component' | 'utility' | 'library' | 'asset';
  dependencies: string[];
  impact: 'low' | 'medium' | 'high';
}

interface BundleAnalysis {
  totalSize: number;
  modules: BundleModule[];
  largestModules: BundleModule[];
  optimizationOpportunities: string[];
  treeShakingPotential: number;
  codeSplittingOpportunities: string[];
  timestamp: number;
}

export function useBundleAnalyzer() {
  const [bundleAnalysis, setBundleAnalysis] = useState<BundleAnalysis>({
    totalSize: 0,
    modules: [],
    largestModules: [],
    optimizationOpportunities: [],
    treeShakingPotential: 0,
    codeSplittingOpportunities: [],
    timestamp: 0
  });
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Analyze bundle composition
  const analyzeBundle = useCallback(async () => {
    setIsAnalyzing(true);
    const startTime = Date.now();

    try {
      // Simulate bundle analysis
      const mockModules: BundleModule[] = [
        {
          name: 'react-native',
          size: 2048576,
          type: 'library',
          dependencies: [],
          impact: 'high'
        },
        {
          name: '@react-navigation/native',
          size: 1048576,
          type: 'library',
          dependencies: ['react-native'],
          impact: 'high'
        },
        {
          name: 'SlotGrid',
          size: 15360,
          type: 'component',
          dependencies: ['useSlotQuery'],
          impact: 'medium'
        },
        {
          name: 'ContextValidator',
          size: 8192,
          type: 'component',
          dependencies: ['@react-navigation/native'],
          impact: 'low'
        },
        {
          name: 'useSlotQuery',
          size: 4096,
          type: 'utility',
          dependencies: [],
          impact: 'medium'
        },
        {
          name: 'assets/images/logo.png',
          size: 51200,
          type: 'asset',
          dependencies: [],
          impact: 'low'
        }
      ];

      const totalSize = mockModules.reduce((sum, module) => sum + module.size, 0);
      const largestModules = [...mockModules].sort((a, b) => b.size - a.size).slice(0, 5);

      // Identify optimization opportunities
      const optimizationOpportunities: string[] = [];
      const codeSplittingOpportunities: string[] = [];

      mockModules.forEach(module => {
        if (module.size > 100000 && module.type === 'library') {
          optimizationOpportunities.push(`Large library: ${module.name} (${(module.size / 1024 / 1024).toFixed(2)}MB)`);
        }

        if (module.type === 'component' && module.dependencies.length > 3) {
          codeSplittingOpportunities.push(`Complex component: ${module.name} (${module.dependencies.length} dependencies)`);
        }
      });

      // Calculate tree shaking potential
      const unusedModules = mockModules.filter(m => m.impact === 'low' && m.size > 1000);
      const treeShakingPotential = (unusedModules.reduce((sum, m) => sum + m.size, 0) / totalSize) * 100;

      const analysis: BundleAnalysis = {
        totalSize,
        modules: mockModules,
        largestModules,
        optimizationOpportunities,
        treeShakingPotential,
        codeSplittingOpportunities,
        timestamp: Date.now()
      };

      setBundleAnalysis(analysis);

      console.log('[BundleAnalyzer] Analysis completed:', {
        totalSize: (totalSize / 1024 / 1024).toFixed(2) + 'MB',
        modules: mockModules.length,
        treeShakingPotential: treeShakingPotential.toFixed(1) + '%',
        analysisTime: Date.now() - startTime
      });

    } catch (error) {
      console.error('[BundleAnalyzer] Analysis failed:', error);
    } finally {
      setIsAnalyzing(false);
    }
  }, []);

  // Initial analysis
  useEffect(() => {
    analyzeBundle();
  }, [analyzeBundle]);

  return {
    bundleAnalysis,
    isAnalyzing,
    analyzeBundle
  };
}

export default function BundleAnalyzer() {
  const { bundleAnalysis, isAnalyzing } = useBundleAnalyzer();
  const { totalSize, largestModules, optimizationOpportunities, treeShakingPotential, codeSplittingOpportunities } = bundleAnalysis;

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Bundle Analyzer</Text>

      {isAnalyzing && (
        <Text style={styles.analyzing}>Analyzing bundle...</Text>
      )}

      <View style={styles.summaryContainer}>
        <Text style={styles.summaryTitle}>Bundle Summary</Text>
        <Text style={styles.summaryText}>Total Size: {(totalSize / 1024 / 1024).toFixed(2)} MB</Text>
        <Text style={styles.summaryText}>Modules: {bundleAnalysis.modules.length}</Text>
        <Text style={styles.summaryText}>Tree Shaking Potential: {treeShakingPotential.toFixed(1)}%</Text>
        <Text style={styles.summaryText}>Optimization Opportunities: {optimizationOpportunities.length}</Text>
      </View>

      <View style={styles.largestContainer}>
        <Text style={styles.largestTitle}>Largest Modules</Text>
        {largestModules.map((module, index) => (
          <View key={index} style={styles.moduleItem}>
            <Text style={styles.moduleName}>{module.name}</Text>
            <Text style={styles.moduleSize}>{(module.size / 1024 / 1024).toFixed(2)} MB</Text>
            <Text style={[styles.moduleImpact, styles[module.impact]]}>{module.impact.toUpperCase()}</Text>
            <Text style={styles.moduleType}>{module.type}</Text>
          </View>
        ))}
      </View>

      {optimizationOpportunities.length > 0 && (
        <View style={styles.optimizationsContainer}>
          <Text style={styles.optimizationsTitle}>Optimization Opportunities</Text>
          {optimizationOpportunities.map((opportunity, index) => (
            <Text key={index} style={styles.optimizationText}>â€¢ {opportunity}</Text>
          ))}
        </View>
      )}

      {codeSplittingOpportunities.length > 0 && (
        <View style={styles.splittingContainer}>
          <Text style={styles.splittingTitle}>Code Splitting Opportunities</Text>
          {codeSplittingOpportunities.map((opportunity, index) => (
            <Text key={index} style={styles.splittingText}>â€¢ {opportunity}</Text>
          ))}
        </View>
      )}

      <View style={styles.recommendationsContainer}>
        <Text style={styles.recommendationsTitle}>Recommendations</Text>
        <Text style={styles.recommendationText}>â€¢ Enable tree shaking for unused modules</Text>
        <Text style={styles.recommendationText}>â€¢ Implement code splitting for large components</Text>
        <Text style={styles.recommendationText}>â€¢ Optimize large library imports</Text>
        <Text style={styles.recommendationText}>â€¢ Compress and optimize assets</Text>
        <Text style={styles.recommendationText}>â€¢ Use dynamic imports for lazy loading</Text>
      </View>
    </ScrollView>
  );
}

